mapper.update:
UPDATE dv_cleantransform
SET clean_name = #{cleanName},
description = #{descr},
update_date = #{updateDate},
update_user = #{updateUser}
WHERE pk_id = #{id}

mapper.select:
SELECT pk_id, clean_name, description, is_deleted, create_user, update_user, DATE_FORMAT(create_date,'%Y-%m-%d %T') as create_date, DATE_FORMAT(update_date,'%Y-%m-%d %T') as update_date FROM dv_cleantransform
is_deleted = 0 AND clean_name = #{cleanName}
AND pk_id != #{id}
