mapper.select:
SELECT pk_id, step_id, task_id, attr_item, attr_value, attr_description, is_deleted, DATE_FORMAT(create_date,'%Y-%m-%d %T') as create_date, create_user, DATE_FORMAT(update_date,'%Y-%m-%d %T') as update_date, update_user FROM dv_accesstask_stepattr
is_deleted = 0 and task_id=#{taskId}
order by step_id
