    private static List<GeoPoint> getPoints(String pointStr){
        String[] split = pointStr.split(";");
        List<GeoPoint> points = new ArrayList<>(split.length);
        GeoPoint point;
        for(String kv : split){
            point = resetFromString(kv);
            if(point != null)
                points.add(point);
        }

        return Collections.unmodifiableList(points);
    }

    private static GeoPoint resetFromString(String value) {
        int comma = value.indexOf(',');
        double lat;
        double lon;
        if (comma != -1) {
            lon = Double.parseDouble(value.substring(0, comma).trim());
            lat = Double.parseDouble(value.substring(comma + 1).trim());

            return new GeoPoint(lat, lon);
        }
        return null;
    }

    public static Map<String, List<GeoPoint>> getWhAreaPointsMap(){
        Map<String, List<GeoPoint>> map = new HashMap<>();
        map.putAll(pointMap);
        return map;
    }


